import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import { useParams } from "react-router-dom";
import tourData from '../assets/data/tours';

const TourDetails = () => {
  const { id } = useParams();

  // Find the tour with the given ID
  const tour = tourData.find(tour => tour.id === id);

  // Handle case where tour is not found
  if (!tour) {
    return <div>Tour not found</div>;
  }

  // Destructure properties from the tour object
  const { photo, title, desc, city, distance, price, reviews, maxGroupSize } = tour;

  return (
    <section>
      <Container>
        <Row>
          <Col lg='8'>
            <div className="tour__content">
              <img src={photo} alt='' />
              <div className="tour__info">
                <h2>{title}</h2>
                {/* Render other details of the tour */}
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default TourDetails;
